const todayDate = new Date().toISOString().split("T")[0];
const playerMap = {};

// Step 1: Group battles by player tag
for (const item of items) {
  const battle = item.json;
  const tag = battle?.team?.[0]?.tag;
  const name = battle?.team?.[0]?.name;
  const rawTime = battle?.battleTime;

  if (!tag || !rawTime) continue;

  if (!playerMap[tag]) {
    playerMap[tag] = {
      tag,
      name,
      battles: [],
    };
  }

  playerMap[tag].battles.push(battle);
}

// Step 2: Check if each player played war today
const results = [];

for (const player of Object.values(playerMap)) {
  let playedWarToday = false;

  for (const b of player.battles) {
    const rawTime = b.battleTime;

    if (rawTime && rawTime.includes("T")) {
      const datePart = rawTime.slice(0, 8);
      const timePart = rawTime.slice(9, 15);
      const formatted = `${datePart.slice(0, 4)}-${datePart.slice(4, 6)}-${datePart.slice(6, 8)}T${timePart.slice(0, 2)}:${timePart.slice(2, 4)}:${timePart.slice(4, 6)}.000Z`;

      const dateOnly = new Date(formatted).toISOString().split("T")[0];

      if (b.type === "clanWar" && dateOnly === todayDate) {
        playedWarToday = true;
        break;
      }
    }
  }

  results.push({
    json: {
      tag: player.tag,
      name: player.name,
      playedWarToday: playedWarToday
    }
  });
}

return results;

const players = {};

// Group battles by player tag
for (const item of items) {
  const battle = item.json;

  if (!battle?.team?.[0]?.tag || !battle?.battleTime) continue;

  const tag = battle.team[0].tag;

  if (!players[tag]) {
    players[tag] = {
      tag,
      name: battle.team[0]?.name || 'Unknown',
      battles: []
    };
  }

  players[tag].battles.push(battle);
}

// Process each player to keep only the last 3 battles and calculate wins
const processed = Object.values(players).map(player => {
  // Sort battles by most recent
  player.battles.sort((a, b) => new Date(b.battleTime) - new Date(a.battleTime));

  // Get last 3
  const last3Battles = player.battles.slice(0, 3).map(battle => {
    const crowns = battle.team?.[0]?.crowns ?? 0;
    const opponentCrowns = battle.opponent?.[0]?.crowns ?? 0;
    const result = crowns > opponentCrowns ? "win" : crowns < opponentCrowns ? "loss" : "draw";

    return {
      battleTime: battle.battleTime,
      crowns,
      result
    };
  });

  // Count wins
  const wins = last3Battles.filter(b => b.result === "win").length;

  return {
    tag: player.tag,
    name: player.name,
    last3Battles,
    wins
  };
});

// Sort players by win count (descending) and take top 3
const top3 = processed
  .sort((a, b) => b.wins - a.wins)
  .slice(0, 3)
  .map(player => ({
    json: {
      tag: player.tag,
      name: player.name,
      last3Battles: player.last3Battles,
      wins: player.wins
    }
  }));

return top3;

const players = {};
const now = new Date();
const daysThreshold = 3;

// Function to convert CR battleTime to valid ISO timestamp
function fixTime(raw) {
  const datePart = raw.slice(0, 8);
  const timePart = raw.slice(9, 15);
  return `${datePart.slice(0, 4)}-${datePart.slice(4, 6)}-${datePart.slice(6, 8)}T${timePart.slice(0, 2)}:${timePart.slice(2, 4)}:${timePart.slice(4, 6)}.000Z`;
}

// Group battles by player
for (const item of items) {
  const battle = item.json;

  if (!battle?.team?.[0]?.tag || !battle?.battleTime) continue;

  const tag = battle.team[0].tag;
  const name = battle.team[0].name;

  if (!players[tag]) {
    players[tag] = {
      tag,
      name,
      battles: []
    };
  }

  players[tag].battles.push(battle);
}

const result = [];

for (const player of Object.values(players)) {
  // Sort battles by most recent first
  player.battles.sort((a, b) => new Date(fixTime(b.battleTime)) - new Date(fixTime(a.battleTime)));

  if (player.battles.length > 0) {
    const lastBattleTime = fixTime(player.battles[0].battleTime);
    const lastBattleDate = new Date(lastBattleTime);
    const diffInDays = (now - lastBattleDate) / (1000 * 60 * 60 * 24);

    if (diffInDays > daysThreshold) {
      result.push({
        json: {
          tag: player.tag,
          name: player.name,
          lastPlayed: lastBattleTime
        }
      });
    }
  }
}

return result;

// Step 1: Extract player names who didn't participate
const warSkippers = items.map((item, index) => {
  const name = item.json.name || `Unknown ${index + 1}`;
  return `${index + 1}. ${name}`;
});

// Step 2: Join into a single formatted list
const formattedList = warSkippers.join('\n');

// Step 3: Output a single item with this combined list
return [
  {
    json: {
      skippersList: formattedList
    }
  }
];

// Input: 3 items (each with a single top player)
// Output: 1 item with a JSON object that includes the array of top players

const top3Combined = items.map(item => item.json);

return [
  {
    json: {
      players: top3Combined
    }
  }
];

